--
-- Cấu trúc bảng cho bảng `noidung_hequocte`
--

CREATE TABLE `noidung_hequocte` (
  `id` int(11) NOT NULL,
  `muc` varchar(255) DEFAULT NULL,
  `tieude` text DEFAULT NULL,
  `noidung` longtext DEFAULT NULL,
  `hinhanh` varchar(255) DEFAULT NULL,
  `ngaydang` datetime DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Đang đổ dữ liệu cho bảng `noidung_hequocte`
--

INSERT INTO `noidung_hequocte` (`id`, `muc`, `tieude`, `noidung`, `hinhanh`, `ngaydang`) VALUES
(6, 'vanban', 'Chương trình liên kết Đào tạo quốc tế', '<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 11.5pt; font-family: Roboto,sans-serif; color: #081b3a; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Chương tr&igrave;nh cung cấp cho người học những kiến thức nền tảng v&agrave; chuy&ecirc;n ng&agrave;nh cần thiết để c&oacute; thể th&agrave;nh c&ocirc;ng trong nhiều lĩnh vực kinh doanh.</span></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 11.5pt; font-family: Roboto,sans-serif; color: #081b3a; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Ngo&agrave;i ra, người học cũng sẽ thu được nhiều lợi &iacute;ch nhờ v&agrave;o m&ocirc;i trường học tương t&aacute;c hiện đại, hệ thống chương tr&igrave;nh với những g&oacute;c nh&igrave;n v&agrave; l&yacute; luận mới mẻ, cũng như đội ngũ giảng vi&ecirc;n truyền cảm hứng đầy nhiệt huyết.</span></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 11.5pt; font-family: Roboto,sans-serif; color: #081b3a; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><img src=\"https://tuyensinh.tmu.edu.vn/upload/category/original/category-images-0-1681456211-1681456211.jpg\" /></span></p>\r\n<p><span id=\"docs-internal-guid-6e2a794a-7fff-a532-e45e-a1437815d7e1\"></span></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 11.5pt; font-family: Roboto,sans-serif; color: #081b3a; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Sau khi ho&agrave;n th&agrave;nh chương tr&igrave;nh học, người học sẽ được cấp bằng từ c&aacute;c Đại học danh tiếng</span></p>', '1744905314_category-images-0-1681456211-1681456211.jpg', '2025-04-17 20:29:08'),
(7, 'vanban', 'Các trường Đại học đối tác', '<p><span id=\"docs-internal-guid-210c78fd-7fff-7872-f5ae-e4fdb2cb30fa\"></span></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 11.5pt; font-family: Roboto,sans-serif; color: #081b3a; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">C&aacute;c đối t&aacute;c li&ecirc;n kết của trường Đại học Thương mại đều l&agrave; c&aacute;c trường đại học c&oacute; uy t&iacute;n của Cộng h&ograve;a Ph&aacute;p, &Aacute;o, Anh, Canada, Nhật Bản, Trung Quốc, Đ&agrave;i Loan,...</span></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><span style=\"font-size: 11.5pt; font-family: Roboto,sans-serif; color: #081b3a; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\"><img src=\"https://tuyensinh.tmu.edu.vn/upload/supporttools/original/support-tools-1681458213.jpg\" alt=\"Trường Đại học West of England\" /></span></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><strong><span style=\"font-size: 11.5pt; font-family: Roboto, sans-serif; color: #081b3a; background-color: transparent; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Trường Đại học IMC Krems</span></strong></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><img src=\"https://dtqt.tmu.edu.vn/images/doi-tac/paris_3.JPG\" alt=\"paris 3\" /></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><strong>Trường đại học Paris II</strong></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><img src=\"https://tuyensinh.tmu.edu.vn/upload/supporttools/original/support-tools-1681458161.jpg\" alt=\"Trường Đại học D&acirc;n tộc Quảng T&acirc;y\" /></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><strong>Trường đại học D&acirc;n tộc Quảng T&acirc;y</strong></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\">&nbsp;</p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><img src=\"https://tuyensinh.tmu.edu.vn/upload/supporttools/original/support-tools-1681458197.jpg\" alt=\"Trường Đại học Toulon\" /></p>\r\n<p dir=\"ltr\" style=\"line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; text-align: center;\"><strong>Trường đại học Toulon</strong></p>', '1744897305_category-images-3-1681456211-1681456211.jpg', '2025-04-17 20:41:22');

